  //       this.props.breakpoint === 'sm' ||
  //       this.props.breakpoint === 'md')
  //   ) {
  //     this.openSidebar('close');
  //   }
  // };


  render() {
    const { children } = this.props;
    return (
      <main className="cr-stapp">        
        <Content fluid>
          <StHeader />
          {children}
          <Footer />
        </Content>
      </main>
    );
  }
}

export default StMainLayout;
